function [cnt, err] = checkRange(in,small,large)%%AUTHOR:  Wandell, Baseler%%ARGUMENTS:% in:  vector of values to check% small: Either a number or a vector of length equal to in% large: Either a number or a vector of length equal to in%RETURNS:% % cnt:  number of values out of range% err:  2 x length(in) matrix of values that are in and out of%       range.  The high values are in(err(1,:)) and the low%       values are in(err(2,:))%% in = [-5:5]% small = 0, large = 3% To avoid round-off error problems, we scale the input values to%only one part in 10^8%factor = 10^8;in = round(in*factor) / factor;% Find the locations that exceed the large values%errL =  in > large;errS =  in < small;cnt = sum(abs(errL)) + sum(abs(errS));err = [errL;errS];